function drawBoard(){let htmlBuild="";for(i=0;i<memoryGlobal.deck.length;i++)htmlBuild+=`<div class="card" onclick="cardSelected(${i})">\n                        <img src="img/0.jpg">\n                        <img src="img/${memoryGlobal.deck[i].face}.jpg" class="hidden">\n                        </div>\n                    </div>`;document.querySelector("main").innerHTML=htmlBuild}function showHideCard(card,show=!0){const selectedCard=document.querySelector("main").childNodes[2*card],selector=show?1:-1;selectedCard.childNodes[2-selector].classList.add("hidden"),selectedCard.childNodes[2+selector].classList.remove("hidden")}